import React, { useState } from "react";

import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";

// This component is a Statefull component => because manage internal State

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2021");
  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />

        {/* Making dinamically the Expense Item */}
        {
          filteredExpenses.map((expense) => (
            <ExpenseItem
              //Always important to add a key for identify
              key={expense.id} //special item for react identify a unique value
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))
          // .filter()
        }
      </Card>
    </div>
  );
};

export default Expenses;
